  constructor(props) {
    super(props);
  }

  render() {
    return (
      <Layout>
        <h1>hola soy el CURSO del usuario : {this.props.queryby}</h1>
      </Layout>
    );
  }
}

UserCursos.getInitialProps = async ({ query }) => {
  console.log(query);
 
 var id_usuario = query.id_user.split('_')[1];  
 getUserwithCursosByUserId(id_usuario).then(response => { 
     console.log(response);
     }
);

 
   
  return { queryby: query.id_user  };
};
export default UserCursos;
